import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import logo from '../assets/neu.png';
import './Navbar.css';

class NavBar extends Component{
    state={clicked:false};

    handleClick=()=>{
        this.setState({clicked:!this.state.clicked})
    }
    render(){
        return(
            <>
             <nav>
                <div className="logo">
                    <img src={logo} alt="Logo"></img>
                    <span>OJT APP</span>
                </div>
                    <ul id="navbar" className={this.state.clicked ? 'navbar active' : 'navbar'}>
                        <li><Link className="activeHome">Home</Link></li>
                        <li><Link to="./pages/EditInfo">Edit Information</Link></li>
                        <li><Link to="./pages/UploadRequirements">Upload Requirements</Link></li>
                        <li><Link to="./pages/GenerateLetter">Endorsement Letter</Link></li>
                    </ul>
    
                <div id="mobile" onClick={this.handleClick}>
                    <i id="bar" className={this.state.clicked ? 'fas fa-times' : 'fas fa-bars'}></i>
                </div>
            </nav>
            </>
        )
    }

}

export default Navbar;
